import '../styles/PushOffCanvas.css'
import { Col, Container, Row } from 'react-bootstrap';

export default function PushOffCanvas({ children, size="250px" }){

   function pushCanvas(e){
        document.querySelector(".pushcanvas").style.width = size;
        document.querySelector(".pushcanvas-content").style.marginLeft = size;
        document.querySelector(".pushcanvas-btn").style.marginLeft = "200px";
        document.body.style.backgroundColor = "rgba(0,0,0,0.4)";
        //document.querySelector(".pushcanvas-btn").style.display = "none";
   }

   function pullCanvas(e){
        document.querySelector(".pushcanvas").style.width = "0";
        document.querySelector(".pushcanvas-content").style.marginLeft = "0";
        document.querySelector(".pushcanvas-btn").style.marginLeft = "0";
        //document.querySelector(".pushcanvas-btn").style.display = "block";
        document.body.style.backgroundColor = "white";
   }

    return (
        <div class="pushcanvas">
            <button class="pushcanvas-btn" onClick={pushCanvas}>
                <i class="bi bi-arrow-right"></i>
            </button>
            <div class="pushcanvas-body">
                <header className="p-3">
                    <Container>
                        <Row>
                            <Col>
                                POPULAR
                            </Col>
                            <Col className="text-end" sm="auto">
                                <span className="bi bi-x-lg pushcanvas-closeicon"  onClick={pullCanvas}></span>
                            </Col>
                        </Row>
                    </Container>
                </header>
                <section>
                    { children }
                </section>
            </div>
        </div>
    )
}
