import '../styles/PushOffCanvas.css'
import { Col, Container, Row } from 'react-bootstrap';


export default function PushOffCanvas({ children, size=250, backdrop }){

    if(size > window.screen.width)
        size = window.screen.width;

   function pushCanvas(e){
        document.querySelector(".pushcanvas").style.width = size + "px";
        document.querySelector(".pushcanvas-content").style.marginLeft = size + "px";
        document.querySelector(".pushcanvas-backdrop").style.display = 'block';
        //document.querySelector(".pushcanvas-btn").style.display = "none";
   }

   function pullCanvas(e){
        document.querySelector(".pushcanvas").style.width = "0";
        document.querySelector(".pushcanvas-content").style.marginLeft = "0";
        document.querySelector(".pushcanvas-backdrop").style.display = 'none';

        //document.querySelector(".pushcanvas-btn").style.display = "block";
   }

    return (
        <div className="pushcanvas">
            <button className="pushcanvas-btn" onClick={pushCanvas}>
                <i className="bi bi-star-fill"></i>
            </button>
            <div className="pushcanvas-backdrop"></div>
            <div className="pushcanvas-body">
                <header className="p-4">
                    <Container>
                        <Row>
                            <Col>
                                POPULAR
                            </Col>
                            <Col className="text-end">
                                <span className="bi bi-x-lg pushcanvas-closeicon"  onClick={pullCanvas}></span>
                            </Col>
                        </Row>
                    </Container>
                </header>
                <section>
                    <Container>
                        { children }
                    </Container>
                </section>
            </div>
        </div>
    )
}
