import '../styles/PushOffCanvas.css'
import { useState } from 'react'

export default function PushOffCanvas({ children, id }){

   function toggleBtn(e){
        e.target.classList.toggle('hide');
   }

    return (
        <div class="pushcanvas">
            <button class="pushcanvas-btn" onMouseOver={toggleBtn}>
                <i class="bi bi-arrow-right"></i>
            </button>
            <div class="pushcanvas-body">
                { children }
            </div>
        </div>
    )
}
