export default function OffCanvas({ children, 
                                    id, 
                                    label, 
                                    buttonClass="btn-close text-reset",
                                    title}){

    return (
        <div class="offcanvas offcanvas-start" data-bs-scroll="true" data-bs-backdrop="false" tabindex="-1" id={id} aria-labelledby={label}>

            <div class="offcanvas-header">
                <h5 class="offcanvas-title" id={label}>{title}</h5>
                <button type="button" class={buttonClass} data-bs-dismiss="offcanvas" aria-label="Close"></button>
            </div>
            <div class="offcanvas-body">
                {children}
            </div>
        </div>
    )
}
