import '../styles/PushOffCanvas.css'
import { useState } from 'react'

export default function PushOffCanvas({ children }){

   function toggleBtn(e){
        e.target.classList.toggle('pushcanvas-on');
        document.querySelector(".pushcanvas").style.width = "250px";
        document.querySelector(".pushcanvas-content").style.marginLeft = "250px";
   }

    return (
        <div class="pushcanvas">
            <button class="pushcanvas-btn" onClick={toggleBtn}>
                <i class="bi bi-arrow-right"></i>
            </button>
            <div class="pushcanvas-body">
                { children }
            </div>
        </div>
    )
}
